import React from 'react'
import { Link } from 'react-router-dom';
import '../style/navbar.scss'
import DashboardIcon from "@mui/icons-material/Dashboard";
import PersonOutlineIcon from "@mui/icons-material/PersonOutline";
import FindInPageIcon from '@mui/icons-material/FindInPage';
import gambar from '../img/balam.png'
import  ExitToAppIcon from '@mui/icons-material/ExitToApp';
import AddBoxIcon from '@mui/icons-material/AddBox';

const Navbar = () => {
  return (
    <div className='sidebar'>
      <div className='top'>
        <Link to='/' style={{ textDecoration: 'none'}}>
          {/* <span className='logo'>Dana Kematian</span> */}
          <img src={gambar} style={{ borderRadius: '50%', width: '50px', margin: '50px'}}/>
        </Link>
      </div>
      <div className='center'>
        <ul>
          <p className='title'>MENU UTAMA</p>
          <li>
            <DashboardIcon className='icon' />
            <span>Dashboard</span>
          </li>
          <p className='title'>Santunan Kematian</p>
          <li>
            <AddBoxIcon className='icon' />
            <span>Tambah</span>
          </li>
          <p className='title'>Bantuan Tidak Mampu</p>
          <li>
            <FindInPageIcon className='icon' />
            <span>Cari NIK</span>
          </li>
          <p className='title'>PENGGUNA</p>
          <li>
            <PersonOutlineIcon className='icon' />
            <span>Users</span>
          </li>
          <li>
            <ExitToAppIcon className="icon" />
            <span>Logout</span>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default Navbar;
