import React, { useEffect, useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";
import "../style/tambah.scss";
import Topbar from "../component/Topbar";
import Navbar from "../component/Navbar";

function Edit() {
  const navigate = useNavigate();

  const { id } = useParams();

  // const [title, setTitle] = useState("");
  // const [description, setDescription] = useState("");
  // const [newTitle, setNewTitle] = useState("");
  // const [newDescription, setNewDescription] = useState("");
  // const [image, setImage] = useState(null);

  const [nik, setNik] = useState("");
  const [nama, setNama] = useState("");
  const [nikWaris, setNikWaris] = useState("");
  const [namaWaris, setNamaWaris] = useState("");
  const [status, setStatus] = useState("");
  const [kematian, setKematian] = useState("");
  const [diterima, setDiterima] = useState("");

  const [validationError, setValidationError] = useState({});

  useEffect(() => {
    fetchProduct();
  });

  const fetchProduct = async () => {
    await axios
      .get(`http://localhost:8000/api/data_kematian/${id}`)
      .then(({ data }) => {
        const { nik, nama, nikWaris, namaWaris, status, kematian, diterima } = data.product;
        setNik(nik);
        setNama(nama);
        setNikWaris(nikWaris);
        setNamaWaris(namaWaris);
        setStatus(status);
        setKematian(kematian);
        setDiterima(diterima);
      })
      .catch(({ response: { data } }) => {
        Swal.fire({
          text: data.message,
          icon: "error",
        });
      });
  };

  const updateProduct = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("nik", nik);
    formData.append("nama", nama);
    formData.append("nikWaris", nikWaris);
    formData.append("namaWaris", namaWaris);
    formData.append("status", status);
    formData.append("kematian", kematian);
    formData.append("diterima", diterima);

    await axios
      .post(`http://localhost:8000/api/products/${id}`, formData)
      .then(({ data }) => {
        Swal.fire({
          icon: "success",
          text: data.message,
        });
        navigate("/");
      })
      .catch(({ response }) => {
        if (response.status === 422) {
          setValidationError(response.data.errors);
        } else {
          Swal.fire({
            text: response.data.message,
            icon: "error",
          });
        }
      });
  };

  return (
    <div className="new">
      <Navbar />
      <div className="newContainer">
        <Topbar />
        <div className="top">
          <h1>Edit Data Pengambilan</h1>
        </div>
        <div className="bottom">
        <div className="form-wrapper">
                {Object.keys(validationError).length > 0 && (
                  <div className="row">
                    <div className="col-12">
                      <div className="alert alert-danger">
                        <ul className="mb-0">
                          {Object.entries(validationError).map(
                            ([key, value]) => (
                              <li key={key}>{value}</li>
                            )
                          )}
                        </ul>
                      </div>
                    </div>
                  </div>
                )}
                <Form onSubmit={updateProduct}>
                <Row>
                <Col>
                  <Form.Group controlId="NIK">
                    <Form.Label>NIK</Form.Label>
                    <Form.Control
                      type="text"
                      value={nik}
                      onChange={(event) => {
                        setNik(event.target.value);
                      }}
                    />
                  </Form.Group>
                </Col>
              </Row>
              <Row>
                <Col>
                  <Form.Group controlId="Nama">
                    <Form.Label>Nama</Form.Label>
                    <Form.Control
                      type="text"
                      value={nama}
                      onChange={(event) => {
                        setNama(event.target.value);
                      }}
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col>
                  <Form.Group controlId="NIKWaris">
                    <Form.Label>NIK Ahli Waris</Form.Label>
                    <Form.Control
                      type="text"
                      value={nikWaris}
                      onChange={(event) => {
                        setNikWaris(event.target.value);
                      }}
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col>
                  <Form.Group controlId="NamaWaris">
                    <Form.Label>Nama Ahli Waris</Form.Label>
                    <Form.Control
                      type="text"
                      value={namaWaris}
                      onChange={(event) => {
                        setNamaWaris(event.target.value);
                        console.log(event);
                      }}
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col>
                  <Form.Group controlId="Kematian">
                    <Form.Label>Waktu Kematian</Form.Label>
                    <Form.Control
                      type="date"
                      value={kematian}
                      onChange={(event) => {
                        setKematian(event.target.value);
                      }}
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col>
                  <Form.Group controlId="Status">
                    <Form.Label>Status Pengambilan</Form.Label>
                    <Form.Select
                      value={status}
                      onChange={(event) =>{
                        setStatus(event.target.value)
                      }}
                    >
                      <option
                        value="Selesai"
                        onChange={(value) => {
                          setStatus(value);
                        }}
                      >
                        Selesai
                      </option>
                      <option
                        value="Proses"
                        onChange={(value) => {
                          setStatus(value);
                        }}
                      >
                        Proses
                      </option>
                    </Form.Select>
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col>
                  <Form.Group controlId="Diterima">
                    <Form.Label>Waktu Diterima</Form.Label>
                    <Form.Control
                      type="date"
                      value={diterima}
                      onChange={(event) => {
                        setDiterima(event.target.value);
                      }}
                    />
                  </Form.Group>
                </Col>
              </Row>
                  {/* <Row>
                    <Col>
                      <Form.Group controlId="Name">
                        <Form.Label>Nama</Form.Label>
                        <Form.Control
                          type="text"
                          placeholder={title}
                          value={newTitle}
                          onChange={(event) => {
                            setTitle(event.target.value);
                          }}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row className="my-3">
                    <Col>
                      <Form.Group controlId="Description">
                        <Form.Label>Description</Form.Label>
                        <Form.Control
                          as="textarea"
                          rows={3}
                          placeholder={description}
                          value={newDescription}
                          onChange={(event) => {
                            setDescription(event.target.value);
                          }}
                        />
                      </Form.Group>
                    </Col>
                  </Row>
                  <Row>
                    <Col>
                      <Form.Group controlId="Image" className="mb-3">
                        <Form.Label>Image</Form.Label>
                        <Form.Control type="file" onChange={changeHandler} />
                      </Form.Group>
                    </Col>
                  </Row> */}
                  <Button
                    variant="primary"
                    className="mt-2"
                    size="lg"
                    block="block"
                    type="submit"
                  >
                    Update
                  </Button>
                </Form>
              </div>
        </div>
      </div>
    </div>
  );
}

export default Edit;
