import edit from './img/edit.png'
import Swal from "sweetalert2";
import remove from './img/remove.png'
import React, { useEffect } from "react";
import { useState } from "react";
import userEvent from "@testing-library/user-event";
import axios from "axios";
import { Link } from "react-router-dom";
export default function App() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");
  const [searchParam] = useState(["nik", "nama"]);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8000/api/data_kematian")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(items);
          setIsLoaded(true);
          setItems(result);
        },

        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  const fetchProducts = async () => {
    await axios
    .get(`http://localhost:8000/api/data_kematian`)
    .then(({data}) => {
      setProducts(data);
      console.log(data.nama)
    });
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  function search(items) {
    return items.filter((item) => {
      return searchParam.some((newItem) => {
        return (
          item[newItem].toString().toLowerCase().indexOf(q.toLowerCase()) > -1
        );
      });
    });
  }

  const deleteProduct = async (id) => {
    const isConfirm = await Swal.fire({
      title: "Are you sure?",
      text: "You won't be able to revert this!",
      icon: "warning",
      showCancelButton: true,
      confirmButtonColor: "#3085d6",
      cancelButtonColor: "#d33",
      confirmButtonText: "Yes, delete it!",
    }).then((result) => {
      return result.isConfirmed;
    });

    if (!isConfirm) {
      return;
    }

    await axios
      .delete(`http://localhost:8000/api/data_kematian/${id}`)
      .then(({ data }) => {
        Swal.fire({
          icon: "success",
          text: data.message,
        });
        fetchProducts();
      })
      .catch(({ response: { data } }) => {
        Swal.fire({
          text: data.message,
          icon: "error",
        });
      });
  };

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading ...</div>;
  } else {
    return (
      <div className="wrapper">
        <div className="search-wrapper">
          <label htmlFor="search-form">
            <input
              type="search"
              name="search-form"
              id="search-form"
              className="search-input"
              placeholder="Search for..."
              value={q}
              /*
                                // set the value of our useState q
                                //  anytime the user types in the search box
                                */
              onChange={(event) => setQ(event.target.value)}
            />
            <span className="sr-only">Search countries here</span>
          </label>
        </div>
        {/* <ul className="card-grid">
          {search(items).map((item) => (
            <li>
              <article className="card" key={item.id}>
                <div className="card-image">
                  <li>{item.nik}</li>
                </div>
                <div className="card-content">
                  <h2 className="card-name">{item.nama}</h2>
                  <ol className="card-list">
                    <li>
                      population: <span>{item.kematian}</span>
                    </li>
                    <li>
                      Region: <span>{item.diterima}</span>
                    </li>
                    <li>
                      Capital: <span>{item.status}</span>
                    </li>
                  </ol>
                </div>
              </article>
            </li>
          ))}
        </ul> */}
        <div className="col-12">
        <div className="card card-body">
          <div className="table-responsive">
            <table className="table table-bordered mb-0 text-center">
              <thead>
                <tr>
                  <th>NIK</th>
                  <th>Nama</th>
                  <th>Nama Waris</th>
                  <th>Kematian</th>
                  <th>Status</th>
                  <th>Diterima</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {items.length > 0 &&
                  search(items).map((row, key) => (
                    <tr key={key}>
                      <td>{row.nik}</td>
                      <td>{row.nama}</td>
                      <td>{row.namaWaris}</td>
                      <td>{row.kematian}</td>
                      <td>{row.status}</td>
                      <td>{row.diterima}</td>
                      <td>
                        <div className="action">
                          {/* <Link
                            to={`/kematian/kematian/edit/${row.id}`}
                            style={{ textDecoration: "none" }}
                          >
                            <img src={edit} width="30px" />
                          </Link> */}
                          <div
                            onClick={() => deleteProduct(row.id)}
                            className="hapus"
                          >
                            <img src={remove} width="25px" />
                          </div>
                        </div>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>
      {/* <div className="tambah">
        <Link to="kematian/baru" className="link">
          Tambah Data
        </Link>
      </div> */}
      </div>
    );
  }
}

