import { DataGrid, GridRowProp, GridColDef } from '@mui/x-data-grid';
import React from 'react'
import { Button } from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';
import '../style/HalamanDaftarKematian.scss'
import { useState } from 'react';

function HalamanDaftarKematian() {
   ;

    const handleDelete = (id) => {
        setData(data.filter((item) => item.id !== id));
      };

    const rows: GridRowsProp = [
      ];
      
      const columns: GridColDef[] = [
        { field: 'col1', headerName: 'Nomor NIK', width: 150 },
        { field: 'col2', headerName: 'Nama', width: 150 },
        { field: 'col2', headerName: 'Tanggal Lahir', width: 250 },
        { field: 'col2', headerName: 'Tanggal Kematian', width: 250 },
        { field: 'col2', headerName: 'Status Pengambilan Dana', width: 250 },
      ];
      
      const [data, setData] = useState(rows)



      const actionColumn = [
        {
          field: "action",
          headerName: "Action",
          width: 200,
          renderCell: (params) => {
            return (
              <div className="cellAction">
                <Link to="/users/test" style={{ textDecoration: "none" }}>
                  <div className="viewButton">View</div>
                </Link>
                <div
                  className="deleteButton"
                  onClick={() => handleDelete(params.row.id)}
                >
                  Delete
                </div>
              </div>
            );
          },
        },
      ];
  return (
    <div className='datatable'>
        <div className='datatableTitle'>
            Data Pengambilan Dana Santunan Kematian
            <Link to="/" className='link'>
                Tambah Data
            </Link>
        </div>

        <DataGrid
            className='datagrid'
            rows={rows}
            columns={columns.concat(actionColumn)}
            pageSize={9}
            rowsPerPageOptions={[9]}
        />
    </div>








    // <div className='container'>
    //     <div className='row'>
    //         <div className='col-12'>
    //             <Link className='btn btn-primary mb-2 float-end' to={"/"}>
    //                 Tambah Data
    //             </Link>
    //         </div>
    //         <div>
                
    //         </div>
    //     </div>
    // </div>
  )
}

export default HalamanDaftarKematian;

