import React, { useState } from "react";
import Navbar from "../component/Navbar";
import Topbar from "../component/Topbar";
import HalamanTambah from "./HalamanTambah";
import "../style/tambah.scss";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";

const Tambah = () => {
  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [validationError, setValidationError] = useState({});
  const [image, setImage] = useState();
  const changeHandler = (event) => {
    setImage(event.target.files[0]);
  };

  const createProduct = async (e) => {
    e.preventDeafault();

    const formData = new FormData();

    formData.append("title", title);
    formData.append("description", description);
    formData.append("image", image);

    await axios
      .post("http://localhost:800/api/products", formData)
      .then(({ data }) => {
        Swal.fire({
          icon: "success",
          text: data.message,
        });
        navigate("/");
      })
      .catch(({ response }) => {
        if (response.status === 422) {
          setValidationError(response.data.errors);
        } else {
          Swal.fire({
            text: response.data.message,
            icon: "error",
          });
        }
      });
  };
  return (
    <div className="new">
      <Navbar />
      <div className="newContainer">
        <Topbar />
        <div className="top">
          <h1>Tambah Data Pengambilan</h1>
        </div>
        <div className="bottom">
          {Object.keys(validationError).length > 0 && (
            <div className="row">
              <div className="col-12">
                <div className="alert alert-danger">
                  <ul className="mb-0">
                    {Object.entries(validationError).map(([key, value]) => (
                      <li key={key}>{value}</li>
                    ))}
                  </ul>
                </div>
              </div>
            </div>
          )}
          <Form onSubmit={createProduct}>
            <Row>
              <Col>
                <Form.Group controlId="Title">
                  <Form.Label>Nama</Form.Label>
                  <Form.Control
                    type="text"
                    value={title}
                    onChange={(event) => {
                      setTitle(event.target.value);
                    }}
                  />
                </Form.Group>
              </Col>
            </Row>

            <Row className="my-3">
                <Col>
                    <Form.Group controlId="Description">
                        <Form.Label>Description</Form.Label>
                        <Form.Control 
                            as="textarea" 
                            rows={3} 
                            value={description} 
                            onChange={(event) =>{
                                setDescription(event.target.value);
                        }}
                        />
                    </Form.Group>
                </Col>
            </Row>

            <Row>
                <Col>
                    <Form.Group controlId="Image" className="mb-3">
                        <Form.Label>Image</Form.Label>
                        <Form.Control 
                            type="file"
                            onChange={changeHandler}
                        />
                    </Form.Group>
                </Col>
            </Row>
            <Button variant="primary" className="mt-2" size="ig" block="block" type="submit">
                Tambah
            </Button>
          </Form>
        </div>
      </div>
    </div>
  );
};

export default Tambah;
