import React, { useEffect } from "react";
import { useState } from "react";
import userEvent from "@testing-library/user-event";
import axios from "axios";
export default function App() {
  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);
  const [q, setQ] = useState("");
  const [searchParam] = useState(["nik", "nama"]);

  // .get(`http://localhost:8000/api/data_kematian`)
  // const getItems = async () => {
  //   const berries = await fetch("http://localhost:8000/api/data_kematian")
  //   const value = await berries.json()
  //   console.log(value)
  //   .then(
  //     (result) => {
  //       setIsLoaded(true);
  //       setItems(result);
  //       console.log(setItems)
  //     },
  //     (error) => {
  //       setIsLoaded(true);
  //       setError(error);
  //     },

  //   )

  // }
  // useEffect(() => {
  //     getItems()
  //   }, [])
  useEffect(() => {
    fetch("http://localhost:8000/api/data_kematian")
      .then((res) => res.json())
      .then(
        (result) => {
          console.log(result);
          setIsLoaded(true);
          setItems(result);
        },
        // Note: it's important to handle errors here
        // instead of a catch() block so that we don't swallow
        // exceptions from actual bugs in components.
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      );
  }, []);

  function search(items) {
    return items.filter((item) => {
      return searchParam.some((newItem) => {
        return (
          item[newItem].toString().toLowerCase().indexOf(q.toLowerCase()) > -1
        );
      });
    });
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  } else if (!isLoaded) {
    return <div>Loading ...</div>;
  } else {
    return (
      // <div>
      //   <ul>
      //     {items.map(item => (
      //       <li key={item.id}>
      //         {item.nama} {item.nik}
      //       </li>
      //     ))}
      //   </ul>
      // </div>
      <div className="wrapper">
        <div className="search-wrapper">
          <label htmlFor="search-form">
            <input
              type="search"
              name="search-form"
              id="search-form"
              className="search-input"
              placeholder="Search for..."
              value={q}
              /*
                                // set the value of our useState q
                                //  anytime the user types in the search box
                                */
              onChange={(event) => setQ(event.target.value)}
            />
            <span className="sr-only">Search countries here</span>
          </label>
        </div>
        <ul className="card-grid">
          {search(items).map((item) => (
            <li>
              <article className="card" key={item.id}>
                <div className="card-image">
                  <li>{item.nik}</li>
                </div>
                <div className="card-content">
                  <h2 className="card-name">{item.nama}</h2>
                  <ol className="card-list">
                    <li>
                      population: <span>{item.kematian}</span>
                    </li>
                    <li>
                      Region: <span>{item.diterima}</span>
                    </li>
                    <li>
                      Capital: <span>{item.status}</span>
                    </li>
                  </ol>
                </div>
              </article>
            </li>
          ))}
        </ul>
      </div>
    );
  }
}
