import React from "react";
import { useState } from "react";

const Login = () => {
  const [pesanError, setPesanError] = useState({});
  const [isKirim, setIsKirim] = useState(false);

  const database = [
    {
      username: "user",
      password: "pass",
    },
    {
      username: "userdua",
      password: "passdua",
    },
  ];

  const errors = {
    uname: "invalid username",
    pass: "invalid password",
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    var { uname, pass } = document.forms[0];

    const userData = database.find((user) => user.username == uname.value);

    if (userData) {
      if (userData.password !== pass.value) {
        setPesanError({ name: "pass", message: errors.pass });
      } else {
        setIsKirim(true);
      }
    } else {
      setPesanError({ name: "uname", message: errors.uname });
    }
  };

  const renderPesanError = (name) => {
    name === pesanError.name && (
      <div className="errors">{pesanError.message}</div>
    );
  };

  const renderForm = (
    <div className="form">
      <form onSubmit={handleSubmit}>
        <div className="input-container">
          <label>Username</label>
          <input type="text" name="uname" required />
          {renderPesanError("uname")}
        </div>

        <div className="input-container">
          <label>Password</label>
          <input type="password" name="pass" required />
          {renderPesanError("pass")}
        </div>
        <div className="button-container">
          <input type="submit" />
        </div>
      </form>
    </div>
  );
  return (
    <div className="app">
      <div className="login-form">
        <div className="title">Sign In</div>
        {isKirim ? <div> User is Successfully logged in </div> : renderForm}
      </div>
    </div>
  );
};

export default Login;
