import { DataGrid, GridRowProp, GridColDef } from "@mui/x-data-grid";
import React, { useEffect } from "react";
import { Button } from "react-bootstrap/Button";
import { Link } from "react-router-dom";
import "../style/HalamanDaftarKematian.scss";
import { useState } from "react";
import axios from 'axios'

function HalamanDaftarKematian() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  });

  const fetchProducts = async () => {
    await axios.get(`http://localhost:8000/api/products`).then(({ data }) => {
      setProducts(data);
    });
  };

  const handleDelete = (id) => {
    setProducts(products.filter((item) => item.id !== id));
  };

  const rows = [
    {
      id: 119140129,
      nama: "Gunawan Sitepu",
      id_waris: 120140129,
      meninggal: "belum",
      status: "belum",
      nama_waris: "Denila Sitepu",
      diterima: "20/10/2022",
    },
  ];

  const userColumns = [
    { field: "id", headerName: "NIK", width: 150 },
    {
      field: "title",
      headerName: "Nama",
      width: 200,
    },
    {
      field: "description",
      headerName: "Nama Waris",
      width: 200,
    },

    {
      field: 'image',
      headerName: "Meninggal",
      width: 150,
    },
    {
      field: "status",
      headerName: "Status",
      width: 120,
      renderCell: (params) => {
        return (
          <div className={`cellWithStatus ${params.row.status}`}>
            {params.row.status}
          </div>
        );
      },
    },
    {
      field: "diterima",
      headerName: "Diterima",
      width: 150,
    },
  ];

  const [data, setData] = useState(rows);

  const actionColumn = [
    {
      field: "action",
      headerName: "Action",
      width: 150,
      renderCell: () => {
        return (
          <div className="cellAction">
            <Link to="/users/test" style={{ textDecoration: "none" }}>
              <div className="viewButton">Edit</div>
            </Link>
            <div
              className="deleteButton"
              onClick={() => handleDelete(products.id)}
            >
              Delete
            </div>
          </div>
        );
      },
    },
  ];
  return (
    <div className="datatable">
      <div className="datatableTitle">
        Data Pengambilan Dana Santunan Kematian
        <Link to="/" className="link">
          Tambah Data
        </Link>
      </div>

      <DataGrid
        className="datagrid"
        rows={products}
        columns={userColumns.concat(actionColumn)}
        pageSize={9}
        rowsPerPageOptions={[9]}
      />
    </div>
  );
}

export default HalamanDaftarKematian;

