import React, { useState } from "react";
import Navbar from "../component/Navbar";
import Topbar from "../component/Topbar";
import HalamanTambah from "./HalamanTambah";
import "../style/tambah.scss";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { useNavigate } from "react-router-dom";
import axios from "axios";


const Tambah = () => {
    const navigate = useNavigate();

    const [title, setTitle] = useState("")
    const [description, setDescription] = useState("")
    const [validationError, setValidationError] = useState({})
    const [image, setImage] = useState()
    const changeHandler = (event) => {
        setImage(event.target.files[0]);
    }

    const createProduct = async (e) => {
        e.preventDeafault();

        const formData = new FormData()
        
        formData.append('title', title)
        formData.append('description', description)
        formData.append('image', image)

        await axios.post('http://localhost:800/api/products', formData).then(({data}) => {
            Swal.fire({
                icon:"success",
                text:data.message
            })
            navigate("/")
        }).catch(({response}) => {
            if(response.status===422){
                setValidationError(response.data.errors)
            }else {
                Swal.fire({
                    text:response.data.message,
                    icon:"error"
                })
            }
        })
    }
  return (
    <div className="new">
      <Navbar />
      <div className="newContainer">
        <Topbar />
        <div className="top">
          <h1>Tambah Data Pengambilan</h1>
        </div>
        {/* {Object.keys(validationError).length > 0 && (
          <div>
            <div>
              <div></div>
            </div>
          </div>
        )} */}

        <div className="bottom">
            <Form onSubmit={createProduct}>
                <Row>
                    <Col>
                        <Form.Group controlId="Name">
                            <Form.Label>Nama</Form.Label>
                            <Form.Control type="text" value={nama} onChange={(event) => {setNama(event.target.value)}}/>
                        </Form.Group>
                    </Col>
                </Row>
            </Form>
        </div>
      </div>
    </div>
  );
};

export default Tambah;
